package com.softtek.presentacion;

import com.softtek.modelo.Calculadora;

public class ProbarCalculadora {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println(Calculadora.sumar(3, 2));
		System.out.println(Calculadora.restar(3, 2));
		System.out.println(Calculadora.multiplicar(3, 2));
		System.out.println(Calculadora.dividir(3, 2));
		System.out.println(Calculadora.elevar(3, 2));
	}

}
